var cwd = process.cwd();

function router(){
    this._modelDir = "model";
    this.init();
}

router.prototype.init = function (){
    var modelDir = this._modelDir;
    global.MVC = new Object();
    global.MVC.MODEL = function(name){
        return require("./"+modelDir+"/"+name);
    }
}

exports = module.exports = router;


