var g_functions = require("./functions");

function Pictures(){
    this.init();
}

Pictures.prototype.init = function(){

    var db_type =g_functions.GetDB(DB_MONGO);

    this.db = new db_type();
    this.tabName="table_name";

}

Pictures.prototype.add=function(data,callback){
    this.db.insert(this.tabName,data,callback);
}

Pictures.prototype.del=function(filter,callback){
    this.db.remove(this.tabName,filter,callback);
}
/*
 * data {data:{},filter:{},options:{}}
 * */
Pictures.prototype.modify = function(search,dst,callback){
    var sqlData = {data:dst,filter:search};
    this.db.update(this.tabName,sqlData,callback);
}

Pictures.prototype.find = function(data,callback){
    this.db.find(this.tabName,data,callback);
}

Pictures.prototype.toJson = function(){
}

exports = module.exports = Pictures;

